import { useEffect } from "react";

export function useComponentDidMount(callback) {
  useEffect(() => {
    if (typeof callback === "function") callback();
  }, [callback]);
}
export function useBeforeDestory() {}

export function useComponentDidUpdate(callback, dependencies) {}
