        <span> asd </span>{" "}
      </App>{" "}
      <App {...{ c: "1", b: 2 }} />{" "}
      {App({
        c: "1",
        b: 2,
        children: <span> asd </span>,
      })}{" "}
    </ErrorBoundary>
  );
};
reRenderUI();
