import { reRenderUI } from "..";
let data = {
  value: undefined,
};

function init(value) {
  data.value = value;
}

function useData(initValue) {
  if (!data.value) {
    init(initValue);
  }
  let update = function (value) {
    data.value = value;
    reRenderUI();
  };
  return [data, update];
}
export default useData;
