// 使用函数定义组件 只关心渲染的是谁
// 组件的渲染就是执行该函数
// 组件不仅仅需要初次渲染 还需要若干次更新

function App() {
  const [a, updateA] = useData(1);
  return <div onClick={() => updateA(a + 1)}>我是App组件{a}</div>;
}
